import React from 'react';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import { Component } from 'react';

export default class Dropdown extends Component {
    constructor(props) {
        super(props);
        this.state = {
            anchorEl: null,
            view: "day",
        };

        this.handleClick = this.handleClick.bind(this);
        this.handleClose = this.handleClose.bind(this);
    }

    handleClick = () => {
        this.setState({ anchorEl: true });
    };

    handleClose = (event) => {
        this.setState({
            anchorEl: null,
            view: event.target.value,
        })
    };

    render() {
        return (
            <div>
                <Button
                    aria-controls="simple-menu"
                    aria-haspopup="true"
                    onClick={handleClick}
                    variant="outlined"
                    color={props.color}
                    size="small"
                >
                    {view}
                </Button >
                <Menu
                    id="simple-menu"
                    anchorEl={anchorEl}
                    keepMounted
                    open={Boolean(anchorEl)}
                    onClose={handleClose}
                >
                    <MenuItem onClick={handleClose} value="day">Day</MenuItem>
                    <MenuItem onClick={handleClose} value="week">Week</MenuItem>
                    <MenuItem onClick={handleClose} value="month">Month</MenuItem>
                </Menu>
            </div >
        );
    }

}

