import React from 'react';
import { Navbar, Nav, NavDropdown, Button } from 'react-bootstrap';
import FullCalendar from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'
import timeGridPlugin from '@fullcalendar/timegrid'
import interactionPlugin from '@fullcalendar/interaction'
import { INITIAL_EVENTS, createEventId } from './event-utils'


class App extends React.Component {

    state = {
        weekendsVisible: true,
        currentEvents: []
    }

    render() {
        return (
            <div>
                <header>
                    <Navbar bg="light" expand="lg">
                        <Navbar.Brand href="#home" style={{ margin: "0 30px 0 10px" }}>TimeFlex</Navbar.Brand>
                        <Navbar.Toggle aria-controls="basic-navbar-nav" />
                        <Navbar.Collapse id="basic-navbar-nav">
                            <Nav className="ml-auto">
                                <Button variant="outline-secondary" style={{ margin: "0 10px 0 10px" }}>Today</Button>
                                <NavDropdown title="Dropdown" id="basic-nav-dropdown" style={{ margin: "0 10px 0 10px" }}>
                                    <NavDropdown.Item href="#action/3.1">Day</NavDropdown.Item>
                                    <NavDropdown.Item href="#action/3.2">Week</NavDropdown.Item>
                                    <NavDropdown.Item href="#action/3.3">Month</NavDropdown.Item>
                                    <NavDropdown.Item href="#action/3.3">Year</NavDropdown.Item>
                                </NavDropdown>
                            </Nav>
                        </Navbar.Collapse>
                    </Navbar>
                </header>
                <body>
                    <FullCalendar
                        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
                        headerToolbar={{
                            left: 'prev,next today',
                            center: 'title',
                            right: 'dayGridMonth,timeGridWeek,timeGridDay'
                        }}
                        initialView='dayGridMonth'
                        editable={true}
                        selectable={true}
                        selectMirror={true}
                        dayMaxEvents={true}
                        weekends={this.state.weekendsVisible}
                        fixedWeekCount={false}
                        height={"100%"}
                        initialEvents={INITIAL_EVENTS} // alternatively, use the `events` setting to fetch from a feed
                        select={this.handleDateSelect}
                        eventClick={this.handleEventClick}
                        eventsSet={this.handleEvents} // called after events are i
                    />
                </body>
            </div >
        );
    }

    handleWeekendsToggle = () => {
        this.setState({
            weekendsVisible: !this.state.weekendsVisible
        })
    }

}

export default App;
